<script>

  import anime from 'animejs/lib/anime.es.js';
  import Header from '@/components/Header';
  import Keys from '@/components/Keys';
  import Lottie from 'vue-lottie/src/lottie';
  import animationData from '@/static/lf20_1jfbv90j.json';
  import Player from '@vimeo/player';


  export default {
      name: "Home",
      components: {
          Header,
          Keys,
         'lottie': Lottie
      },
      mounted() {
          this.MainAnimate();
          this.AboutAnimate();
          $('.marquee-white').marquee({
            duration: 5000,
            delayBeforeStart: 2500,
            direction: 'left',
            duplicated: true,
            speed: 90
          });
          $('.marquee-black').marquee({
            duration: 5000,
            delayBeforeStart: 0,
            direction: 'left',
            duplicated: true,
            speed: 100
          });
          this.HandsAnimate();
          this.anim.play();
          this.VimeoVideo();
      },
      data(){
          return{
              about: {
                  start: false,
                  played: false
              },
              defaultOptions: {
                animationData: animationData,
                autoplay: true
              },
              animationSpeed: 1
          }
      },
      methods: {
          MainAnimate(){

              let LineLeft = document.querySelector('.main-line__left');
              let LineRight = document.querySelector('.main-line__right');

              window.addEventListener('scroll', function () {
                  let pageY = window.pageYOffset;
                  let mainHeight = document.querySelector('.main').offsetHeight;

                  let calcLine = () =>{

                      if (pageY < mainHeight){
                          return pageY / 100 * 10;
                      }

                  };
                  // Left line
                  anime({
                      targets: LineLeft,
                      height: calcLine() + "%"
                  });
                  anime({
                      targets: LineRight,
                      height: calcLine() + "%"
                  });
              });
          },
          AboutAnimate(){
              window.addEventListener('scroll', function () {
                  let AboutTopOffset = document.querySelector('.about').getBoundingClientRect().top + document.body.scrollTop;

              });
          },
          HandsAnimate(){
            window.addEventListener('scroll', function(){
              let HandsOneTopOffset = document.querySelector('.document').getBoundingClientRect().top + document.body.scrollTop;
              if (HandsOneTopOffset <= 50){
                document.querySelector('.document-img img').style.left = 0;
                document.querySelector('.phone-img img').style.left = 0;
              }
            })
          },
          VimeoVideo(){
            let player = new Player('made-in-ny', {
              id: 461358249,
              width: window.innerWidth,
              byline: false,
              controls: false,
              autoplay: true,
              title: false
            });


            player.play()
                .then(function() {
                  console.log('Play')
                });

            player.on('ended', function(data) {
              console.log('end');
              document.querySelector('#made-in-ny').style.opacity = 0;
              document.querySelectorAll('.video-elem').forEach(function (item){
                item.classList.remove('hide-elem');
              })
            });
          },
          handleAnimation: function (anim) {
            this.anim = anim;
          },

          stop: function () {
            this.anim.stop();
          },

          play: function () {
            this.anim.play();
          },

          pause: function () {
            this.anim.pause();
          },

          onSpeedChange: function () {
            this.anim.setSpeed(this.animationSpeed);
          }
      }
  }
</script>

